  const filterList = flow.list.filter(i => i.name.includes(flow.filter.word) && i.type.includes(flow.filter.type) && i.area.includes(flow.filter.area) && i.mode.includes(flow.filter.mode))

  const count = Math.ceil(filterList.length / flow.page.size)
  const page = (flow.page.index > count) ? count : flow.page.index

  const indexpre = flow.page.size * (page - 1)
  const indexnext = flow.page.size * page

  const pageList = filterList.filter((i,index) => (index >= indexpre) && (index < indexnext))

  const list = pageList.map(i => {
    return { ...i, hover: i.name === flow.listHover, $data: flow }
  })

  property.value = list
}

